elements.expression:
filters
| essql query="SELECT COUNT(*) total_flights FROM kibana_sample_data_flights"
| math "total_flights"
| if {compare lt to=15000} then=
{ formatNumber "0.0a" |
  metric "Low flight count" 
  metricFont={font size=48 family="'Open Sans', Helvetica, Arial, sans-serif" color="red"  align="center" lHeight=48} 
  labelFont={font size=14 family="'Open Sans', Helvetica, Arial, sans-serif" color="red" align="center"}
}
else=
{
  formatNumber "0.0a" |
  metric "All is good!" 
  metricFont={font size=48 family="'Open Sans', Helvetica, Arial, sans-serif" color="green"  align="center" lHeight=48} 
  labelFont={font size=14 family="'Open Sans', Helvetica, Arial, sans-serif" color="green" align="center"}
}
| render

